<?php/** Plugin Name: Live username availability checking* Version: 1.0.2* Author: Zvere* Description: A plugin for live username availability checking.*///Add scipt to check username availabilityadd_action( 'admin_footer', function(){//    if(get_current_screen()->base != 'user' || get_current_screen()->action != 'add') return;?><script type="text/javascript" >var xhr = 0;jQuery(document).ready(function($) {    $('#user_login, input[name="user_login"]')        .after('<span class="uc_disp" id="uc_ajax_loader">'            + '<img src="<?php echo plugin_dir_url( __FILE__ ).'ajax-loader.gif'; ?>" width=16 height=16 title="Loading..."/></span>')        .after('<span class="uc_disp" id="uc_valid">'            + '<img src="<?php echo plugin_dir_url( __FILE__ ).'valid.png'; ?>" width=16 height=16 title="Valid"/></span>')        .after('<span class="uc_disp" id="uc_invalid">'            + '<img src="<?php echo plugin_dir_url( __FILE__ ).'invalid.png'; ?>" width=16 height=16 title="Invalid"/></span>')    $('.uc_disp').hide();    $('#user_login').bind('keyup change', function(){        var data = {            action: 'check_username',            username : $('#user_login').val(),        };        $('.uc_disp').hide();        $('#uc_ajax_loader').show();        if(xhr) xhr.abort();        xhr = $.ajax({        url : ajaxurl,        type: 'get',        async : true,        data : data,        success : function(response) {                $('.uc_disp').hide();                if(response == 0) $('#uc_valid').show();                else $('#uc_invalid').show();            },        });        });	});</script><?php});//echo 0 si n'existe pasadd_action('wp_ajax_check_username', function(){    if(username_exists($_GET['username']) == null)        echo 0;    else echo 1;    die();});